import yfinance

def getData(symbol: str, start: str, end: str):
    return yfinance.download(symbol, start, end)

# print(getData("RBLX", "2022-11-01", "2023-07-01"))
# print(getData("INVALID", "2020-01-01", "2020-03-01"))
# print(getData("MSFT", "2022-01-01", "2022-02-01"))

def addSMA(dataframe, shortWindow, longWindow): #simple moving average
    # short window = 5 days; long window = 20 days
    dataframe["SMA short"] = dataframe[f"Close"].rolling(shortWindow)
    dataframe["SMA long"] = dataframe[f"Close"].rolling(longWindow)
    return dataframe

df = getData("MSFT", "2022-01-01", "2022-02-01")
print(addSMA(df, 5, 20))
